-- ============================================

-- 1. Catch unassigned Suru-Verbs
UPDATE kosakata SET category_id = (SELECT id FROM categories WHERE slug = 'verbs') 
WHERE category_id IS NULL AND jlpt_level = 'N4' AND hiragana LIKE '%??';

-- 2. Catch unassigned I-Adjectives
UPDATE kosakata SET category_id = (SELECT id FROM categories WHERE slug = 'i-adjectives') 
WHERE category_id IS NULL AND jlpt_level = 'N4' AND hiragana LIKE '%?' AND hiragana NOT LIKE '%?%'; -- Simple heuristic

-- 3. Everything else -> Daily Life (General Context)
UPDATE kosakata SET category_id = (SELECT id FROM categories WHERE slug = 'daily-life') 
WHERE category_id IS NULL AND jlpt_level = 'N4';


