UPDATE kosakata SET category_id = (SELECT id FROM categories WHERE slug = 'verbs') 
WHERE category_id IS NULL AND jlpt_level = 'N4' AND hiragana LIKE '%する';

-- 2. Catch unassigned I-Adjectives (heuristic: ends in 'i' and likely adj)
-- Risk: Nouns ending in i (e.g. 'ai' love). But 'ai' is already categorized.
UPDATE kosakata SET category_id = (SELECT id FROM categories WHERE slug = 'i-adjectives') 
WHERE category_id IS NULL AND jlpt_level = 'N4' AND hiragana LIKE '%い';

