-- >>> PHASE: Minggu 1-2: Identitas & Waktu (N5) <<<
-- Day 1 (Week 1-1)
DO $$ DECLARE d_id UUID; t_id UUID; BEGIN
  INSERT INTO learning_days (jlpt_level, day_number, title, description, estimated_minutes) VALUES ('N5', 1, 'Minggu 1-2: Identitas & Waktu - Day 1', 'Fokus: people, numbers...', 45) RETURNING id INTO d_id;
